import { createSlice } from '@reduxjs/toolkit'

const notificationSlice = createSlice({
  name: 'notification',
  initialState: '',
  reducers: {
    notify(state, action) {
        return state + action.payload
    },
    removeNotification() {
        return ''
    }
  }

}) 

export const { notify } = notificationSlice.actions
export default notificationSlice.reducer
