import { useDispatch, useSelector } from 'react-redux'
import { } from '../reducers/anecdoteReducer'
import { notify, removeNotification } from '../reducers/notificationReducer'

const Anecdote = ({ anecdote, handleClick }) => {
    return (
        <li>
            {anecdote.content}
            <strong> has {anecdote.votes}</strong>
            <button onClick={handleClick}>vote</button>
        </li>
    )
}

const AnecdoteList = () => {
    const dispatch = useDispatch()
    const anecdotes = useSelector(({ input, anecdotes }) => {
        return anecdotes
            .filter(anecdote => anecdote.content.toLowerCase().includes(input.toLowerCase()))
    })
    anecdotes.sort((a, b) => b.votes - a.votes)

    return (
        <ul>
            {anecdotes.map(anecdote =>
                <Anecdote
                    key={anecdote.id}
                    anecdote={anecdote}
                    handleClick={() => {
                        dispatch(addVotes(anecdote.id))
                        dispatch(notify(`you voted "${anecdote.content}" `))
                        setTimeout(() => {
                            dispatch(removeNotification())
                        }, 5000)
                    }
                    }
                />
            )}
        </ul>
    )
}

export default AnecdoteList
